import { useState } from "react";
import { Header } from "../../components/Header";
import background from "../../assets/background.svg";
import ItemList from '../../components/ItemList/index';
import "./styles.css";

function App() {
  const [user, setUser] = useState('');
  const [currentUser, currentSetUser] = useState(null);
  const [repo, setRepo] = useState(null);

  const handleGetData = async () => {
    const userData = await fetch(`https://api.github.com/users/${user}`);
    const newUser = await userData.json();

    console.log(newUser);

    if (newUser.name) {
      const {avatar_url, name, bio} = newUser;
      currentSetUser({avatar_url, name, bio})

      const repoData = await fetch(`https://api.github.com/users/${user}/repos`);
      const newRepo = await userData.json();

      if(newRepo.length){
        setRepo(newRepo);
      }
    }
  }

  return (
    <div className="App">
      <Header />
      <div className="conteudo">
        <img src={background} className="background" alt="background app" />
        <div className="info">
          <div>
            <input name="usuario" value={user} 
            onChange={event => setUser(event.target.value)} placeholder="@username" />
            <button onClick={handleGetData}>Buscar</button>
          </div>
          {currentUser?.name ? (
            <>
                      <div className="perfil">
                      <img
                        src="https://avatars.githubusercontent.com/u/126275223?s=96&v=4"
                        className="profile"
                        alt="imagem de perfil"
                      />
          
                      <div>
                      <h3>{}</h3>
                      <span>{}</span>
                      <p>{}</p>
                      </div>
          
                    </div>
            </>
          ) : null}

          <hr />

          {cu}

          <div>
            <h4 className="repositorio">Repositórios</h4>
            <ItemList title={"werref"} description={"wweewewregty"} />
            <ItemList title={"werref"} description={"wweewewregty"} />
            <ItemList title={"werref"} description={"wweewewregty"} />
          </div>

        </div>
      </div>
    </div>
  );
}

export default App;
